angular.module("td").controller("LoginCtrl", function($scope, $firebase, $rootScope, simpleLoginFactory, FIREBASE_URL) {
  var firebaseRef;
  firebaseRef = new Firebase(FIREBASE_URL);
  $scope.user = {
    email: '',
    password: ''
  };
  $scope.loginPassword = function() {
    var promis;
    $scope.errors = [];
    return promis = simpleLoginFactory.$login('password', {
      email: $scope.user.email,
      password: $scope.user.password
    }).then(function(user) {
      $rootScope.currentUser = user;
      return $scope.user = {
        email: '',
        password: ''
      };
    }, function(error) {
      return $scope.errors.push(error);
    });
  };
  return $scope.loginFacebook = function() {
    $scope.errors = [];
    return simpleLoginFactory.$login("facebook").then(function(user) {
      debugger;
      return firebaseRef.child('users').child(user.uid).set(user);
    });
  };
});

angular.module("td").controller("NewUserCtrl", function($state, $rootScope, $scope, Restangular) {
  $scope.user = {};
  return $scope.newUser = function() {
    return Restangular.all('bruker').post($scope.user).then(function(user) {
      if (user.error) {
        return $scope.error = user;
      } else {
        return console.log("Saved " + user.first_name);
      }
    });
  };
});

angular.module("td").controller("RegisterCtrl", function($scope, $state, $firebase, $rootScope, simpleLoginFactory, FIREBASE_URL) {
  var firebaseRef;
  firebaseRef = new Firebase(FIREBASE_URL);
  $scope.registerUser = {
    email: '',
    password: ''
  };
  return $scope.register = function() {
    var user;
    $scope.errors = [];
    user = $scope.registerUser;
    return simpleLoginFactory.$createUser(user.email, user.password).then(function(user) {
      firebaseRef.child('users').child(user.uid).set(user);
      $rootScope.currentUser = user;
      $scope.registerUser = {
        email: '',
        password: ''
      };
      return $state.go('user_update');
    }, function(error) {
      return $scope.errors.push(error);
    });
  };
});

angular.module("td").controller("SessionsCtrl", function($state, $rootScope, $scope, $filter, Restangular) {
  $scope.newSession = function(session, date) {
    var session_params;
    session_params = {
      user_id: $rootScope.current_user.id,
      comment: session.comment,
      date: $filter('date')(date, 'yyyy-MM-dd')
    };
    return Restangular.all('trening').post(session_params).then(function(session) {
      return $scope.sessions.push(session);
    });
  };
  return $scope.destroySession = function(session) {
    var index;
    Restangular.one('trening', session.id).remove();
    index = $scope.sessions.indexOf(session);
    return $scope.sessions.splice(index, session.id);
  };
});

